import React from 'react';
import { motion } from 'framer-motion';
import '../styles/Features.css';

const featureData = [
  {
    icon: '‚ú®',
    title: 'Á≤æÁæéÂä®Áîª',
    description: 'ÈÄöËøáÊµÅÁïÖÁöÑËøáÊ∏°ÂíåÁªÜËÖªÁöÑÂä®ÊÄÅÊïàÊûúÔºå‰∏∫Áî®Êà∑Â∏¶Êù•ÊÑâÊÇ¶ÁöÑËßÜËßâ‰ΩìÈ™å'
  },
  {
    icon: 'üì±',
    title: 'ÂìçÂ∫îÂºèËÆæËÆ°',
    description: 'ÂÆåÁæéÈÄÇÈÖçÂêÑÁßçËÆæÂ§áÂ∞∫ÂØ∏Ôºå‰ªéÊâãÊú∫Âà∞Ê°åÈù¢ÔºåÊèê‰æõ‰∏ÄËá¥ÁöÑ‰ºòË¥®‰ΩìÈ™å'
  },
  {
    icon: 'üöÄ',
    title: 'È´òÊïàÊÄßËÉΩ',
    description: '‰ºòÂåñÁöÑ‰ª£Á†ÅÁªìÊûÑÂíåËµÑÊ∫êÂä†ËΩΩÔºåÁ°Æ‰øùÁΩëÁ´ôÂø´ÈÄüÂìçÂ∫îÂíåÊµÅÁïÖËøêË°å'
  },
  {
    icon: 'üé®',
    title: 'Áé∞‰ª£ÁæéÂ≠¶',
    description: 'ÁÆÄÊ¥Å‰ºòÈõÖÁöÑËÆæËÆ°ËØ≠Ë®ÄÔºåÁªìÂêàÊó∂Â∞öÈÖçËâ≤ÊñπÊ°àÔºåÂ±ïÁé∞‰∏ì‰∏öÂìÅË¥®'
  }
];

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2
    }
  }
};

const itemVariants = {
  hidden: { y: 50, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      duration: 0.5
    }
  }
};

const Features = () => {
  return (
    <section className="features-section section">
      <div className="container">
        <motion.div
          className="section-header"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7 }}
        >
          <h2 className="section-title">‰ºòÁßÄÁâπÊÄß</h2>
          <p className="section-description">
            Êàë‰ª¨Êèê‰æõ‰∫Ü‰∏ÄÁ≥ªÂàóÁé∞‰ª£ÂåñÁöÑÂäüËÉΩÔºåÂ∏ÆÂä©ÊÇ®ÊâìÈÄ†‰ª§‰∫∫Âç∞Ë±°Ê∑±ÂàªÁöÑÁΩëÁ´ô
          </p>
        </motion.div>

        <motion.div
          className="features-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {featureData.map((feature, index) => (
            <motion.div
              className="feature-card"
              key={index}
              variants={itemVariants}
              whileHover={{ y: -10, transition: { duration: 0.3 } }}
            >
              <div className="feature-icon">{feature.icon}</div>
              <h3 className="feature-title">{feature.title}</h3>
              <p className="feature-description">{feature.description}</p>
              <motion.div 
                className="feature-link"
                whileHover={{ x: 5 }}
                transition={{ type: "spring", stiffness: 400 }}
              >
                ‰∫ÜËß£Êõ¥Â§ö ‚Üí
              </motion.div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Features;
